        <div>

            <table className="table table-dark">
                <thead>
                    <tr>
                        <th> Name </th>
                        <th> Lat </th>
                        <th> Long </th>

                    </tr>
                </thead>
                <tbody>
                    { this.handleRestaurantsData(restaurants)}
                </tbody>
            </table>
        </div>

    )
}
}



export default Restaurants;
