import UserContext from "../../UserContext";
import React, { useState } from "react";
import firebase from "firebase";

const Profile = () =>{
    const { user } = React.useContext(UserContext);
    let searches = [];

    const final = [];
    const r = firebase.database().ref("Searches");
    r.on("value", (snapshot) => {
        console.log(snapshot.val());
        searches = Array.from(snapshot.val()).filter((result) =>
            result && result.userID === user.uid
        ).map((result) => result.query);
        for (let  search of searches) {
            final.push(<li key={search}>{search}</li>);
        }
    });


    return(
        <div>
            <div style = {{
                display: "flex",
                justifyContent: "space-around",
                margin: "15 px 0px",
            }}>
                <div>
                    <img style ={{ width: "160px", height :" 160 px", borderRadius: "80px"}}

                 />
                </div>
                <div>
                    <h4>Welcome {user.email}</h4>
                </div>
                <div>
                    <h5>Favorites</h5>
                    <ul>
                        {final}
                    </ul>

                </div>
        </div>
    </div>


    )
}

export default Profile;


