import React, { useState, useEffect } from "react";

import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
  MarkerClusterer,
} from "@react-google-maps/api";

import mapStyles from "./mapStyles";
import { formatRelative } from "date-fns";
import { restaurantCollection, db } from "./utils/firebase.js";
import Restaurants from "./components/restaurants/restaurants";
import { data } from "browserslist";
import { firebaseLooper } from "./utils/helper";

const restaurantData = [
  { id: 1, lat: 47.597998, lng: -122.318739, time: new Date() },
  { id: 2, lat: 47.600757, lng: -122.332526, time: new Date() },
  { id: 3, lat: 47.598528, lng: -122.326986, time: new Date() },
];

const mapContainerStyle = {
  width: "90vw",
  height: "90vh",
};

const center = {
  lat: 47.59994,
  lng: -122.32775,
};

const options = {
  styles: mapStyles,
  disableDefaultUI: true,
  zoomControl: true,
};

const onLoad = (marker) => {
  console.log("marker: ", marker);
};
const position = {
  lat: 47.605,
  lng: -122.3344,
};

const App = () => {
  // state to hold all library object locations that we can pass to maps API. calling helper function to set initial state
  const [restaurantMarkers, setRestaurantMarkers] = useState([]);

  useEffect(() => {
    db.collection("restaurants")
      .get()
      .then((snapshot) => {
        const restaurants = firebaseLooper(snapshot);
        console.log(restaurants);
        setRestaurantMarkers(restaurants);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const [selected, setSelected] = React.useState(null);

  if (loadError) return "Error loading maps";
  //props: container, see notes above where the variable is
  //position the map
  return (
    <div>
      {isLoaded && (
        <GoogleMap
          mapContainerStyle={mapContainerStyle}
          zoom={15}
          center={center}
          options={options}
          id="marker-example"
        >
          {restaurantData.map((restaurant) => (
            <Marker
              key={restaurant.id}
              onLoad={onLoad}
              position={{ lat: restaurant.lat, lng: restaurant.lng }}
              // position = { position }
            //   icon={{
            //     url: "spoon.svg",
            //     scaledSize: new window.google.maps.Size(30, 30),
            //     origin: new window.google.maps.Point(0, 0),
            //     anchor: new window.google.maps.Point(15, 15),
            //   }}
              onClick={() => {
                setSelected(restaurant);
              }}
            />
          ))}

          {selected ? (
            <InfoWindow
              position={{ lat: selected.lat, lng: selected.lng }}
              onCloseClick={() => {
                setSelected(null);
              }} //have to reset to null once x is clicked on window so that they can pop up agian when clicked
            >
              <div>
                <h2> image goes here </h2>
                <p>
                  Restaurants list last updated{" "}
                  {formatRelative(selected.time, new Date())}
                </p>
              </div>
            </InfoWindow>
          ) : null}
        </GoogleMap>
      )}
    </div>
  );
};

export default App;
