import React, { useState } from "react";

import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";

import mapStyles from "../mapStyles";
import firebase from "firebase";
import { search } from "language-tags";
import { r } from "tar";


const restaurantData = [
  { id: 1, lat: 47.620084, lng: -122.320126, time: new Date() },
  { id: 2, lat: 47.614579, lng: -122.333224, time: new Date() },
  { id: 3, lat: 47.618578, lng: -122.321079, time: new Date() },
];

const mapContainerStyle = {
  width: "90vw",
  height: "90vh",
};

const center = {
  lat: 47.620084,
  lng: -122.320126,
};

const options = {
  styles: mapStyles,
  disableDefaultUI: true,
  zoomControl: true,
};

const onLoad = (marker) => {
  console.log("marker: ", marker);
};
const position = {
  lat: 47.605,
  lng: -122.3344,
};


function Map(props) {

  const [restaurants, setRestaurants] = useState([]);

  const Search = (search) => {
    const r = firebase.database().ref('Restaurants');
      r.on('value', (snapshot) => {
        let filteredResults = Array.from(snapshot.val()).filter((result) => result.Name.toLowerCase().includes(search));
        setRestaurants(filteredResults);
      });
      };

  const saveResults = () => {
    const r = firebase.database().ref('Searches');
    r.set({ 'restaurants': restaurants, 'userID': oMakJOXG2cX5GmRnpwHqBwO59Mw2, 'query': query

    })

    console.log(restaurants)};

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const [selected, setSelected] = React.useState(null);
  const[query, setQuery] = React.useState(new URLSearchParams(props.location.search).get('q'));
  //  default value  if not query, if (!query) { setQuery("blinis"); }


  if (loadError) return "Error loading maps";

  return (
    <div>
      <form onSubmit={ (e)=> {
        e.preventDefault()
        Search(e.target[0].value)

        }}>

        <input type= "button" onClick = {saveResults}></input>
        <input onInput= {(e)=>{ console.log(e.target.value); setQuery(e.target.value)} } type="search" value={query}></input>
      </form>

      {isLoaded && (
        <GoogleMap
          mapContainerStyle={mapContainerStyle}
          zoom={13}
          center={center}
          options={options}
          id="marker-example"
        >
          {restaurants.map((restaurant) => (
            <Marker
              key={restaurant.Name}
              onLoad={onLoad}
              position={{ lat: restaurant.Location.lat, lng: restaurant.Location.lng }}

              onClick={() => {
                setSelected(restaurant);
              }}
            />
          ))}

          {selected ? (
            <InfoWindow
              position={{ lat: selected.Location.lat, lng: selected.Location.lng }}
              onCloseClick={() => {
                setSelected(null);
              }} 
            >
              <div>
                <h2> {selected.Name} </h2>
                <p>
                  {selected.Address}

                </p>
              </div>
            </InfoWindow>
          ) : null}
        </GoogleMap>
      )}
    </div>
  );
};

export default Map;
