import React, { useState, useEffect } from "react";

import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";

import mapStyles from "../mapStyles";
import { formatRelative } from "date-fns";
import { restaurantCollection, db } from "../utils/firebase.js";
import Restaurants from "./restaurants/restaurants";
import { data } from "browserslist";
import { firebaseLooper } from "../utils/helper";
import axios from 'axios';
// import RestaurantList from "./listall";





const restaurantData = [
  { id: 1, lat: 47.620084, lng: -122.320126, time: new Date() },
  { id: 2, lat: 47.614579, lng: -122.333224, time: new Date() },
  { id: 3, lat: 47.618578, lng: -122.321079, time: new Date() },
];

const mapContainerStyle = {
  width: "90vw",
  height: "90vh",
};

const center = {
  lat: 47.620084,
  lng: -122.320126,
};

const options = {
  styles: mapStyles,
  disableDefaultUI: true,
  zoomControl: true,
};

const onLoad = (marker) => {
  console.log("marker: ", marker);
};
const position = {
  lat: 47.605,
  lng: -122.3344,
};

const App = () => {
  const [restaurantMarkers, setRestaurantMarkers] = useState([]);
  const [restaurants, setRestaurants] = useState([]);


  useEffect(() => {

    axios.get('https://yuliacapstone-default-rtdb.firebaseio.com/Restaurants.json')
        .then(  response => {
                // this.setState({
                console.log('hi')
                //     restaurants
                // });
                console.log(response.data)
                setRestaurants(response.data)

                // console.log(this.state,'<---restaurants state')
            }).catch(error=> {
                console.log(error)
                console.log('random')
            });
          }, []);







  useEffect(() => {
    db.collection("restaurants")
      .get()
      .then((snapshot) => {
        const restaurants = firebaseLooper(snapshot);
        console.log(restaurants);
        setRestaurantMarkers(restaurants);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const [selected, setSelected] = React.useState(null);

  if (loadError) return "Error loading maps";
  return (
    <div>
      <Restaurants/>
      {isLoaded && (
        <GoogleMap
          mapContainerStyle={mapContainerStyle}
          zoom={13}
          center={center}
          options={options}
          id="marker-example"
        >
          {restaurantData.map((restaurant) => (
            <Marker
              key={restaurant.id}
              onLoad={onLoad}
              position={{ lat: restaurant.lat, lng: restaurant.lng }}
              //   icon={{
              //     url: "spoon.svg",
              //     scaledSize: new window.google.maps.Size(30, 30),
              //     origin: new window.google.maps.Point(0, 0),
              //     anchor: new window.google.maps.Point(15, 15),
              //   }}
              onClick={() => {
                setSelected(restaurant);
              }}
            />
          ))}

          {selected ? (
            <InfoWindow
              position={{ lat: selected.lat, lng: selected.lng }}
              onCloseClick={() => {
                setSelected(null);
              }} 
            >
              <div>
                <h2> Description here </h2>
                <p>
                  Restaurants list last updated{" "}
                  {formatRelative(selected.time, new Date())}
                </p>
              </div>
            </InfoWindow>
          ) : null}
        </GoogleMap>
      )}
    </div>
  );
};

export default App;
